import { Component } from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import SingleBook from "./SingleBook";
import InputGroup from "react-bootstrap/InputGroup";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

class BookList extends Component {
  state = {
    query: "",
  };

  filterBookList = function () {
    this.props.booksArray.filter((books) =>
      books.title.includes(this.state.query).map((book) => {
        return book;
      })
    );
  };

  render() {
    return (
      <Container>
        <Row>
          <Form
            onSubmit={(e) => {
              e.preventDefault();
              this.filterBookList();
            }}
          >
            <InputGroup className="my-3 mt-5">
              <Form.Control
                placeholder="Insert your query"
                aria-label="Insert your query"
                aria-describedby="basic-addon2"
                value={this.state.query}
                onChange={(e) => {
                  this.setState({ query: e.target.value });
                }}
              />
              <Button variant="outline-secondary" type="submit">
                üîç
              </Button>
            </InputGroup>
          </Form>
        </Row>

        <Row>
          {this.props.booksArray.map((book) => {
            return <SingleBook key={book.asin} myBook={book}></SingleBook>;
          })}
        </Row>
      </Container>
    );
  }
}

export default BookList;
