import { Component } from "react";

class SingleComment extends Component {
  render() {
    return (
      <>
        <h6>
          Author: {this.props.author}
          <span> - rate: {this.props.rate}</span>
        </h6>
        <p>{this.props.comment}</p>
      </>
    );
  }
}

export default SingleComment;
